          <?php
          function get_categories_and_courses()
          {
            $categories = get_terms([
              'taxonomy' => 'course-cat',
              'hide_empty' => true,
            ]);

            $data = [];
            foreach ($categories as $category) {
              $courses = new WP_Query([
                'post_type' => 'course',
                'tax_query' => [
                  [
                    'taxonomy' => 'course-cat',
                    'field' => 'term_id',
                    'terms' => $category->term_id,
                  ],
                ],
                'posts_per_page' => 7,
              ]);

              // Prepare data for output
              $data[] = [
                'category' => $category,
                'courses' => $courses->posts,
              ];
            }

            return $data;
          }

